/***********************SET PROPERTTY***********************/ //Nivel de proyecto log.info("Antes= " + testRunner.testCase.testSuite.project.getPropertyValue("Nombre")) testRunner.testCase.testSuite.project.setPropertyValue("Nombre", "1") log.info("Despues= " + testRunner.testCase.testSuite.project.getPropertyValue("Nombre"))
//Nivel de conjunto de prueba log.info("Antes= " + testRunner.testCase.testSuite.getPropertyValue("Nombre2")) testRunner.testCase.testSuite.setPropertyValue("Nombre2", "2") log.info("Despues= " + testRunner.testCase.testSuite.getPropertyValue("Nombre2"))
//Nivel de caso de prueba log.info("Antes= " + testRunner.testCase.getPropertyValue("Nombre3")) testRunner.testCase.setPropertyValue("Nombre3", "3") log.info("Despues= " + testRunner.testCase.getPropertyValue("Nombre3"))
//Nivel de paso de prueba log.info("Antes= " + testRunner.testCase.getTestStepByName("Properties").getPropertyValue("Nombre4")) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Nombre4", "4") log.info("Despues= " + testRunner.testCase.getTestStepByName("Properties").getPropertyValue("Nombre4"))

/***********************ADD PROPERTTY***********************/
