//Nivel Conjunto de prueba
testRunner.testCase.testSuite.removeProperty("Propiedad adicionada")
//Nivel caso de prueba
testRunner.testCase.removeProperty("Propiedad adicionada")
//Nivel paso de prueba
testRunner.testCase.getTestStepByName("Properties").removeProperty("Propiedad adicionada")

