// Nivel de testCase
testRunner.testCase.properties.each{
	key,value->
	log.info(key+" = "+ testRunner.testCase.getPropertyValue(key))	
}

// Si vamos a llamar un método de un script del mismo testCase sería de la siguiente manera:
def testStep=testRunner.testCase.getTestStepByName("Hola");
testStep.run(testRunner, context);
context.Hola.PrimerMetodo();

// Si vamos a llamar un método de un script del otro testCase sería de la siguiente manera:
def proyecto= testRunner.testCase.testSuite.project;
def conjunto=proyecto.testSuites["Clase7"];
def caso= conjunto.testCases["testCase"];
def paso= caso.getTestStepByName("Hello");
paso.run(testRunner, context);
context.Hello.Imprimir();

//def es una forma generar de definir cualquier variable
class Hola{
	def log;
	def context;
	def testRunner;

	//Constructor de la clase
	def Hola(log, context,testRunner){
		this.log=log;
		this.context=context;
		this.testRunner=testRunner;	
	}

	def PrimerMetodo(){
		log.info("Este es el primer metodo de la clase Hola")	
	}
}
//Necesario para que su clase se pueda instanciar
context.setProperty("Hola", new Hola(log, context, testRunner))

//def es una forma generar de definir cualquier variable
class Hello{
	def log;
	def context;
	def testRunner;

	//Constructor de la clase
	def Hello(log, context,testRunner){
		this.log=log;
		this.context=context;
		this.testRunner=testRunner;	
	}

	def Imprimir(){
		log.info("Estas llamando al método de la clase Hello")	
	}
}
//Necesario para que su clase se pueda instanciar
context.setProperty("Hello", new Hello(log, context, testRunner))

def testStep=testRunner.runTestStepByName("CountryISOCode");
def estado= testStep.getStatus().toString();
	if (estado.equals("OK")){
	log.info("Status CountryISOCode >>> PASS")
	}
	if (estado.equals("FAILED")){
	log.info("Status CountryISOCode >>> FAILED")
	}

